import React, { useState, useEffect, useCallback, useMemo } from 'react';
import { auth, provider, signInWithPopup, signOut } from './auth';
import { db } from './firebase';
import { collection, addDoc, Timestamp, query, orderBy, limit, getDocs } from 'firebase/firestore';
import './App.css';
import AdBanner from './AdBanner';

function App() {
  const [user, setUser] = useState(null);
  const [difficulty, setDifficulty] = useState('easy');
  const [layout, setLayout] = useState('grid');
  const [tiles, setTiles] = useState([]);
  const [flipped, setFlipped] = useState([]);
  const [matched, setMatched] = useState([]);
  const [score, setScore] = useState(0);
  const [timer, setTimer] = useState(0);
  const [isActive, setIsActive] = useState(false);
  const [leaderboard, setLeaderboard] = useState([]);

  // Listen for auth state changes
  useEffect(() => {
    const unsubscribe = auth.onAuthStateChanged(u => setUser(u));
    return () => unsubscribe();
  }, []);

  // Login handler
  const handleLogin = async () => {
    try {
      await signInWithPopup(auth, provider);
    } catch (error) {
      alert('Login failed: ' + error.message);
    }
  };

  // Logout handler
  const handleLogout = async () => {
    try {
      await signOut(auth);
    } catch (error) {
      alert('Logout failed: ' + error.message);
    }
  };

  // Grid size based on difficulty
  const gridSizes = { easy: 4, medium: 6, hard: 8 };
  const gridSize = gridSizes[difficulty];

  // Animal images for kids (easy)
  const animalImages = useMemo(() => [
    'https://cdn-icons-png.flaticon.com/512/616/616408.png', // Cat
    'https://cdn-icons-png.flaticon.com/512/616/616430.png', // Dog
    'https://cdn-icons-png.flaticon.com/512/616/616415.png', // Lion
    'https://cdn-icons-png.flaticon.com/512/616/616426.png', // Elephant
    'https://cdn-icons-png.flaticon.com/512/616/616418.png', // Monkey
    'https://cdn-icons-png.flaticon.com/512/616/616420.png', // Rabbit
    'https://cdn-icons-png.flaticon.com/512/616/616424.png', // Bear
    'https://cdn-icons-png.flaticon.com/512/616/616422.png', // Fox
  ], []);

  // Generate tiles (images for kids, numbers for adults)
  const generateTiles = useCallback(() => {
    const totalTiles = gridSize * gridSize;
    let values = [];
    if (difficulty === 'easy') {
      // Use unique animal images for each pair, minimize duplication
      let pairsNeeded = totalTiles / 2;
      let selectedImages = [];
      // If not enough images, repeat after all are used
      for (let i = 0; i < pairsNeeded; i++) {
        selectedImages.push(animalImages[i % animalImages.length]);
      }
      selectedImages.forEach(img => {
        values.push(img, img);
      });
    } else {
      // Use numbers for adults
      for (let i = 1; i <= totalTiles / 2; i++) {
        values.push(i, i);
      }
    }
    // Shuffle
    for (let i = values.length - 1; i > 0; i--) {
      const j = Math.floor(Math.random() * (i + 1));
      [values[i], values[j]] = [values[j], values[i]];
    }
    return values;
  }, [difficulty, gridSize, animalImages]);

  // Reset board when difficulty/layout changes
  useEffect(() => {
    setTiles(generateTiles());
    setFlipped([]);
    setMatched([]);
    setScore(0);
    setTimer(0);
    setIsActive(true);
  }, [difficulty, layout, generateTiles]);

  // Timer effect
  useEffect(() => {
    let interval = null;
    if (isActive && matched.length < tiles.length) {
      interval = setInterval(() => {
        setTimer(t => t + 1);
      }, 1000);
    } else if (!isActive || matched.length === tiles.length) {
      clearInterval(interval);
    }
    return () => clearInterval(interval);
  }, [isActive, matched, tiles.length]);

  // Save score to Firestore when puzzle is completed
  useEffect(() => {
    if (matched.length === tiles.length && matched.length > 0 && user) {
      const saveScore = async () => {
        try {
          await addDoc(collection(db, 'scores'), {
            score,
            time: timer,
            difficulty,
            layout,
            userId: user.uid,
            userName: user.displayName,
            createdAt: Timestamp.now()
          });
        } catch (error) {
          console.error('Error saving score:', error);
        }
      };
      saveScore();
    }
  }, [matched, tiles.length, score, timer, difficulty, layout, user]);

  // Fetch leaderboard from Firestore
  useEffect(() => {
    const fetchLeaderboard = async () => {
      try {
        const q = query(
          collection(db, 'scores'),
          orderBy('score', 'desc'),
          orderBy('time', 'asc'),
          limit(10)
        );
        const querySnapshot = await getDocs(q);
        const scores = querySnapshot.docs.map(doc => doc.data());
        setLeaderboard(scores);
      } catch (error) {
        console.error('Error fetching leaderboard:', error);
      }
    };
    fetchLeaderboard();
  }, [matched]);

  // Handle tile click
  const handleTileClick = idx => {
    if (flipped.length === 2 || flipped.includes(idx) || matched.includes(idx)) return;
    const newFlipped = [...flipped, idx];
    setFlipped(newFlipped);
    if (newFlipped.length === 2) {
      const [first, second] = newFlipped;
      if (tiles[first] === tiles[second]) {
        setTimeout(() => {
          setMatched([...matched, first, second]);
          setFlipped([]);
          setScore(s => s + 10); // Increase score for a match
        }, 700);
      } else {
        setTimeout(() => setFlipped([]), 700);
      }
    }
  };

  // Render grid
  const renderGrid = () => (
    <div style={{
      display: 'grid',
      gridTemplateColumns: `repeat(${gridSize}, 60px)`,
      gap: '10px',
      justifyContent: 'center',
      marginTop: '20px'
    }}>
      {tiles.map((val, idx) => (
        <div
          key={idx}
          onClick={() => handleTileClick(idx)}
          className={`tile${flipped.includes(idx) || matched.includes(idx) ? ' flipped' : ''}`}
          style={{
            width: 60,
            height: 60,
            background: matched.includes(idx) || flipped.includes(idx) ? '#4caf50' : '#eee',
            color: matched.includes(idx) || flipped.includes(idx) ? '#fff' : '#eee',
            display: 'flex',
            alignItems: 'center',
            justifyContent: 'center',
            fontSize: 24,
            borderRadius: 8,
            cursor: 'pointer',
            border: '2px solid #ccc',
            userSelect: 'none',
            overflow: 'hidden'
          }}
        >
          {(matched.includes(idx) || flipped.includes(idx)) ? (
            difficulty === 'easy' ? (
              <img src={val} alt="animal" style={{ width: '40px', height: '40px' }} />
            ) : (
              val
            )
          ) : '?'}
        </div>
      ))}
    </div>
  );

  return (
    <div style={{
      background: 'linear-gradient(135deg, #f5faff 0%, #e3f2fd 100%)',
      minHeight: '100vh',
      padding: '0',
      fontFamily: 'Segoe UI, Arial, sans-serif'
    }}>
      <header style={{
        maxWidth: 900,
        margin: '0 auto',
        padding: '40px 20px 20px 20px'
      }}>
        <h1 style={{
          textAlign: 'center',
          color: '#1976d2',
          fontWeight: 700,
          fontSize: '2.5em',
          marginBottom: '10px'
        }}>Puzzle Game</h1>
        <AdBanner />
        <div style={{
          display: 'flex',
          justifyContent: 'space-between',
          alignItems: 'center',
          margin: '30px 0 20px 0',
          flexWrap: 'wrap',
          gap: '20px'
        }}>
          <div style={{
            background: '#fff',
            borderRadius: '12px',
            boxShadow: '0 2px 8px rgba(33,150,243,0.08)',
            padding: '20px',
            minWidth: 220,
            flex: '1 1 220px'
          }}>
            <h3 style={{ color: '#2196f3', marginBottom: 10 }}>Sign In</h3>
            {!user ? (
              <button onClick={handleLogin} style={{
                fontSize: '1em',
                padding: '10px 20px',
                borderRadius: '8px',
                background: '#2196f3',
                color: '#fff',
                border: 'none',
                cursor: 'pointer',
                width: '100%'
              }}>
                Sign in with Google
              </button>
            ) : (
              <div style={{ display: 'flex', alignItems: 'center', gap: '12px' }}>
                <img src={user.photoURL} alt="profile" style={{ width: 40, height: 40, borderRadius: '50%' }} />
                <span style={{ fontWeight: 500 }}>{user.displayName}</span>
                <button onClick={handleLogout} style={{
                  fontSize: '1em',
                  padding: '6px 16px',
                  borderRadius: '8px',
                  background: '#f44336',
                  color: '#fff',
                  border: 'none',
                  cursor: 'pointer'
                }}>
                  Logout
                </button>
              </div>
            )}
          </div>
          <div style={{
            background: '#fff',
            borderRadius: '12px',
            boxShadow: '0 2px 8px rgba(33,150,243,0.08)',
            padding: '20px',
            minWidth: 220,
            flex: '1 1 220px'
          }}>
            <h3 style={{ color: '#2196f3', marginBottom: 10 }}>Difficulty</h3>
            <select value={difficulty} onChange={e => setDifficulty(e.target.value)} style={{
              fontSize: '1em',
              padding: '8px',
              borderRadius: '6px',
              border: '1px solid #2196f3',
              width: '100%'
            }}>
              <option value="easy">Kids - Easy</option>
              <option value="medium">Medium</option>
              <option value="hard">Adults - Hard</option>
            </select>
          </div>
          <div style={{
            background: '#fff',
            borderRadius: '12px',
            boxShadow: '0 2px 8px rgba(33,150,243,0.08)',
            padding: '20px',
            minWidth: 220,
            flex: '1 1 220px'
          }}>
            <h3 style={{ color: '#2196f3', marginBottom: 10 }}>Layout</h3>
            <select value={layout} onChange={e => setLayout(e.target.value)} style={{
              fontSize: '1em',
              padding: '8px',
              borderRadius: '6px',
              border: '1px solid #2196f3',
              width: '100%'
            }}>
              <option value="grid">Grid</option>
              <option value="hex">Hexagonal</option>
            </select>
          </div>
        </div>
        <div style={{
          display: 'flex',
          justifyContent: 'center',
          gap: '40px',
          marginBottom: '20px'
        }}>
          <div style={{
            fontSize: '1.2em',
            background: '#e3f2fd',
            padding: '10px 24px',
            borderRadius: '8px',
            color: '#1976d2',
            fontWeight: 600
          }}>Score: {score}</div>
          <div style={{
            fontSize: '1.2em',
            background: '#e3f2fd',
            padding: '10px 24px',
            borderRadius: '8px',
            color: '#1976d2',
            fontWeight: 600
          }}>Time: {timer}s</div>
        </div>
        <div style={{
          marginTop: '30px',
          padding: '20px',
          border: '1px solid #ccc',
          borderRadius: '12px',
          background: '#fff',
          color: '#333',
          boxShadow: '0 2px 8px rgba(33,150,243,0.08)'
        }}>
          <h2 style={{ color: '#1976d2', marginBottom: 16 }}>Puzzle Board</h2>
          {layout === 'grid' ? renderGrid() : <p>Other layouts coming soon!</p>}
          {matched.length === tiles.length && (
            <div style={{
              marginTop: '20px',
              color: '#4caf50',
              fontWeight: 'bold',
              fontSize: '1.2em',
              textAlign: 'center'
            }}>
              ðŸŽ‰ Congratulations! You completed the puzzle.<br />Final Score: {score} | Time: {timer}s
            </div>
          )}
        </div>
        <div style={{
          marginTop: '30px',
          padding: '20px',
          border: '1px solid #2196f3',
          borderRadius: '12px',
          background: '#f5faff',
          color: '#333',
          boxShadow: '0 2px 8px rgba(33,150,243,0.08)'
        }}>
          <AdBanner />
          <h2 style={{ color: '#1976d2', marginBottom: 16 }}>Leaderboard</h2>
          <table style={{ width: '100%', borderCollapse: 'collapse' }}>
            <thead>
              <tr style={{ background: '#e3f2fd' }}>
                <th style={{ padding: '8px', border: '1px solid #2196f3' }}>Rank</th>
                <th style={{ padding: '8px', border: '1px solid #2196f3' }}>Score</th>
                <th style={{ padding: '8px', border: '1px solid #2196f3' }}>Time (s)</th>
                <th style={{ padding: '8px', border: '1px solid #2196f3' }}>Difficulty</th>
                <th style={{ padding: '8px', border: '1px solid #2196f3' }}>Layout</th>
              </tr>
            </thead>
            <tbody>
              {leaderboard.length === 0 ? (
                <tr><td colSpan={5} style={{ textAlign: 'center', padding: '12px' }}>No scores yet.</td></tr>
              ) : (
                leaderboard.map((entry, idx) => (
                  <tr key={idx}>
                    <td style={{ padding: '8px', border: '1px solid #2196f3' }}>{idx + 1}</td>
                    <td style={{ padding: '8px', border: '1px solid #2196f3' }}>{entry.score}</td>
                    <td style={{ padding: '8px', border: '1px solid #2196f3' }}>{entry.time}</td>
                    <td style={{ padding: '8px', border: '1px solid #2196f3' }}>{entry.difficulty}</td>
                    <td style={{ padding: '8px', border: '1px solid #2196f3' }}>{entry.layout}</td>
                  </tr>
                ))
              )}
            </tbody>
          </table>
        </div>
        <AdBanner />
      </header>
    </div>
  );
}

export default App;
